mapper.select:
SELECT p.project_id AS projectId, p.client_id AS clientId, c.company_name AS clientName, p.project_name AS projectName, ifnull(CONCAT(DATE_FORMAT(p.created_date, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(p.settle_day, '%Y-%m-%d')), '미정') AS projectDuration, p.manager AS projectManager, p.mphone AS managerPhone, COUNT(DISTINCT a.freelancer_id) AS participant, SUM(ct.fee) AS totalAmount, SUM(ct.total_pay - ct.fee) AS totalFee, ifnull(SUM(ct.total_pay), 0) AS totalSettlement, DATE_FORMAT(p.settle_day, '%Y%m%d') AS settleDate, ifnull(MAX(ct.status), '구인중') AS settleStatus FROM project p LEFT JOIN client c ON p.client_id = c.client_id LEFT JOIN apply a ON a.project_id = p.project_id AND a.is_approved = TRUE LEFT JOIN contract ct ON ct.project_id = p.project_id
(p.project_name LIKE CONCAT('%', #{keyword}, '%') OR c.company_name LIKE CONCAT('%', #{keyword}, '%'))
AND ct.status = #{settleStatus}
AND p.created_date IS NOT NULL AND p.created_date >= #{startDate}
AND p.created_date IS NOT NULL AND p.created_date <= #{endDate}
AND p.created_date IS NOT NULL AND p.settle_day IS NOT NULL
GROUP BY p.project_id ORDER BY p.project_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(DISTINCT p.project_id) FROM project p LEFT JOIN client c ON p.client_id = c.client_id LEFT JOIN apply a ON a.project_id = p.project_id AND a.is_approved = TRUE LEFT JOIN contract ct ON ct.project_id = p.project_id
(p.project_name LIKE CONCAT('%', #{keyword}, '%') OR c.company_name LIKE CONCAT('%', #{keyword}, '%'))
AND ct.status = #{settleStatus}
AND p.created_date IS NOT NULL AND p.created_date >= #{startDate}
AND p.created_date IS NOT NULL AND p.created_date <= #{endDate}
AND p.created_date IS NOT NULL AND p.settle_day IS NOT NULL
