mapper.select:
SELECT profile_img
FROM user
where user_id = #{freelancerId}

mapper.update:
UPDATE freelancer
SET bank = #{bank}, account_num = #{accountNum}, address = #{address}
WHERE freelancer_id = #{freelancerId}

mapper.select:
SELECT s.sub_category_name as category
FROM freelancer f join subCategory s
where f.freelancer_id = #{freelancerId}

SELECT f.freelancer_id, f.sub_category_id, f.skill, f.desired_salary, f.introduction, u.nickname, u.profile_img, sc.sub_category_name, c.category_id, c.category_name, COALESCE(AVG(r.star), 0) AS avg_star FROM freelancer f LEFT JOIN subCategory sc ON f.sub_category_id = sc.sub_category_id LEFT JOIN category c ON sc.category_id = c.category_id LEFT JOIN user u ON f.freelancer_id = u.user_id LEFT JOIN review r ON f.freelancer_id = r.r_user_id WHERE f.sub_category_id IN
#{id}
GROUP BY f.freelancer_id ORDER BY f.freelancer_id DESC
