mapper.select:
SELECT f.freelancer_id AS freelancer_id, u.profile_img AS profile_img, u.name AS name, f.desired_location AS desired_location, f.skill AS skill, (SELECT ROUND(AVG(r.star), 1) FROM review r WHERE r.r_user_id = f.freelancer_id) AS rating, (SELECT COUNT(*) FROM contract c WHERE c.freelancer_id = jf.freelancer_id) AS project_count FROM jjimFree jf JOIN freelancer f ON jf.freelancer_id = f.freelancer_id JOIN user u ON f.freelancer_id = u.user_id WHERE jf.client_id = #{clientId} ORDER BY
rating DESC project_count DESC jf.jjim_id DESC
LIMIT #{offset}, #{limit}

mapper.delete:
DELETE FROM jjimFree
WHERE client_id = #{clientId}
  AND freelancer_id = #{freelancerId}
