mapper.select:
SELECT pj.project_id, pj.advertisement_title, sc.sub_category_name, pj.req_skills, pj.status, pj.created_date, pj.deadline_date, pj.duration, pj.start_date, pj.end_date, pj.project_progress, l.Lv_id, l.Lv, p.project_fee, COUNT(l.Lv_id) AS recruit_count, (SELECT COUNT(*) FROM apply a WHERE a.project_id = pj.project_id) AS apply_count, (SELECT SUM(p2.project_fee) FROM pay p2 WHERE p2.project_id = pj.project_id) AS total_amount FROM project pj JOIN client c ON pj.client_id = c.client_id JOIN user u ON c.client_id = u.user_id LEFT JOIN pay p ON pj.project_id = p.project_id LEFT JOIN lv l ON p.lv_id = l.Lv_id LEFT JOIN subCategory sc ON pj.sub_category_id = sc.sub_category_id WHERE pj.client_id = #{clientId} AND pj.is_deleted = FALSE AND pj.status IS NOT NULL
AND pj.status = #{status}
GROUP BY pj.project_id, pj.advertisement_title, sc.sub_category_name, pj.req_skills, pj.status, pj.created_date, pj.start_date, pj.end_date, pj.project_progress, pj.duration, l.Lv_id, l.Lv, p.project_fee;
