mapper.insert:
INSERT INTO qna (
    qna_id, user_id, question_title, question_content, question_date, answer_content, answer_date
)
VALUES (
           #{qnaId}, #{userId}, #{questionTitle}, #{questionContent}, #{questionDate}, #{answerContent}, #{answerDate}
       )

mapper.select:
SELECT qna_id AS qnaId, user_id AS userId, question_title AS questionTitle, question_content AS questionContent, question_date AS questionDate, answer_content AS answerContent, answer_date AS answerDate FROM qna
(question_title LIKE CONCAT('%', #{keyword}, '%') OR user_id LIKE CONCAT('%', #{keyword}, '%'))
AND (question_title LIKE CONCAT('%', #{category}, '%'))
AND (answer_content IS NOT NULL)
AND (answer_content IS NULL)
AND question_date >= #{startDate}
AND question_date <= #{endDate}
ORDER BY qna_id DESC LIMIT #{offset}, #{limit}
