mapper.select:
SELECT COALESCE(MAX(cnt), 0) + 1
FROM settlelist
WHERE settlelist.slist_id IN (
    SELECT id FROM contract WHERE project_id = #{projectId}
)

SELECT IFNULL(MAX(cnt), 0)
FROM settlelist
WHERE project_id = #{projectId}

SELECT MAX(settle_date)
FROM settlelist
WHERE project_id = #{value}

SELECT * FROM settlelist
WHERE project_id = #{projectId}
  AND settle_date = #{settleDate}
LIMIT 1

SELECT * FROM settlelist
WHERE project_id = #{projectId}
  AND settle_date = #{settleDate}
LIMIT 1

SELECT *
FROM settlelist
WHERE project_id = #{projectId}
  AND settle_date = #{settleDate}
LIMIT 1

SELECT sl.slist_id, sl.project_id AS projectId, p.project_name AS projectName, DATE_FORMAT(sl.settle_date, '%Y-%m-%d') AS settleDate, sl.cnt AS cnt, SUM(s.ammount) AS totalAmount, MAX(s.status) AS status, SUM(c.pay) AS pay, SUM(c.fee) AS fee FROM settlement s JOIN settlelist sl ON s.slist_id = sl.slist_id JOIN contract c ON c.project_pay_id = s.category_id JOIN project p ON sl.project_id = p.project_id
p.project_name LIKE CONCAT('%', #{keyword}, '%')
AND sl.settle_date >= #{startDate}
AND sl.settle_date <= #{endDate}
GROUP BY sl.project_id, sl.settle_date, sl.cnt ORDER BY sl.settle_date DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM (
SELECT 1
FROM settlement s
JOIN settlelist sl ON s.slist_id = sl.slist_id
JOIN project p ON sl.project_id = p.project_id


        p.project_name LIKE CONCAT('%', #{keyword}, '%')


        AND sl.settle_date >= #{startDate}


        AND sl.settle_date <= #{endDate}


GROUP BY sl.project_id, sl.settle_date, sl.cnt
) sub
