mapper.select:
SELECT COUNT(*)
FROM settlement s
         JOIN settlelist sl ON s.slist_id = sl.slist_id
WHERE sl.project_id = #{projectId}
  AND sl.settle_date = #{settleDate}

SELECT COUNT(*)
FROM settlement
WHERE slist_id = #{slistId}
  AND settleday = #{settleDate}
  AND client_id = #{clientId}
